mixins.code:
@use './styles/utilities' as *;
@use './styles/variables' as *;

.my-custom-component {
  @include elevation(2);
  @include responsive-padding(spacing('6'));
  background: get-color('primary', 500);

  @include respond-to('md') {
    @include elevation(3);
  }

  &:hover {
    @include glassmorphism(12px, 0.15);
  }
}
