)

func fibonacci(n int, ch chan<- int) {
	a, b := 0, 1
	for i := 0; i < n; i++ {
		ch <- a
		a, b = b, a+b
	}
	close(ch)
}

func fanOut(nums []int) []int {
	results := make([]int, len(nums))
	var wg sync.WaitGroup
	for i, n := range nums {
		wg.Add(1)
		go func(idx, val int) {
			defer wg.Done()
			results[idx] = val * val
		}(i, n)
	}
	wg.Wait()
	return results
}

func main() {
	ch := make(chan int, 10)
	go fibonacci(10, ch)

	fmt.Print("Fibonacci: ")
	var fibs []int
	for v := range ch {
		fmt.Printf("%d ", v)
		fibs = append(fibs, v)
	}
	fmt.Println()

	squares := fanOut(fibs)
	fmt.Print("Squares:   ")
	for _, s := range squares {
		fmt.Printf("%d ", s)
	}
	fmt.Println()
}

