#[derive(Debug)]
enum Expr {
    Num(f64),
    Add(Box<Expr>, Box<Expr>),
    Sub(Box<Expr>, Box<Expr>),
    Mul(Box<Expr>, Box<Expr>),
    Div(Box<Expr>, Box<Expr>),
}

impl Expr {
    fn compute(&self) -> Result<f64, String> {
        match self {
            Expr::Num(n) => Ok(*n),
            Expr::Add(a, b) => Ok(a.compute()? + b.compute()?),
            Expr::Sub(a, b) => Ok(a.compute()? - b.compute()?),
            Expr::Mul(a, b) => Ok(a.compute()? * b.compute()?),
            Expr::Div(a, b) => {
                let divisor = b.compute()?;
                if divisor == 0.0 {
                    Err("Division by zero".to_string())
                } else {
                    Ok(a.compute()? / divisor)
                }
            }
        }
    }
}

fn num(n: f64) -> Box<Expr> { Box::new(Expr::Num(n)) }
fn add(a: Box<Expr>, b: Box<Expr>) -> Box<Expr> { Box::new(Expr::Add(a, b)) }
fn mul(a: Box<Expr>, b: Box<Expr>) -> Box<Expr> { Box::new(Expr::Mul(a, b)) }
fn div(a: Box<Expr>, b: Box<Expr>) -> Box<Expr> { Box::new(Expr::Div(a, b)) }

fn main() {
    let expr = mul(add(num(3.0), num(4.0)), num(2.0));
    println!("(3 + 4) * 2 = {:?}", expr.compute());

    let bad = div(num(10.0), Box::new(Expr::Sub(num(5.0), num(5.0))));
    match bad.compute() {
        Ok(v) => println!("Result: {}", v),
        Err(e) => println!("Error: {}", e),
    }

    let fibs: Vec<u64> = std::iter::successors(Some((0u64, 1u64)), |&(a, b)| Some((b, a + b)))
        .map(|(a, _)| a)
        .take(10)
        .collect();
    println!("Fibonacci: {:?}", fibs);
}

